import React from "react";
import ReactDOM from "react-dom";
import Blockly from "node-blockly/browser";

import BlocklyDrawer, { Block, Category } from "react-blockly-drawer";

const helloWorld = {
  name: "HelloWorld",
  category: "Operation",
  block: {
    init: function() {
      this.setHelpUrl('');
      this.setColour(230);
      this.appendValueInput("Name")
          .appendField("Olala");
      this.setTooltip('');
    }
  },
  generator: block => {
    const message = `'${block.getFieldValue("NAME")}'` || "''";
    const code = `console.log('Hello ${message}')`;
    return [code, Blockly.JavaScript.ORDER_MEMBER];
  }
};

ReactDOM.render(
  <BlocklyDrawer
    tools={[helloWorld]}
    onChange={(code, workspace) => {
      console.log(code);
    }}
    appearance={{
      categories: {
        Demo: {
          colour: "270"
        }
      }
    }}
  >
    {/* Category "Khối nhập" (Blocks for Input) */}
    <category name="Input">
        {/* Bổ sung label "Nhập số tự nhiên" */}
        <block type="math_number">
            <field name="NUM" text="Nhập số tự nhiên"></field>
        </block>

        {/* <!-- Category "Khối Phép tính" (Blocks for Arithmetic Operations) --> */}
        <category name="Operations">
            {/* <!-- Khối Cộng (Addition) --> */}
            <block type="math_addition">
                <field name="OP">ADD</field>
                <value name="A">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
                <value name="B">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
            </block>

            {/* <!-- Khối Trừ (Subtraction) --> */}
            <block type="math_subtraction">
                <field name="OP">MINUS</field>
                <value name="A">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
                <value name="B">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
            </block>

            {/* <!-- Khối Nhân (Multiplication) --> */}
            <block type="math_multiplication">
                <field name="OP">MULTIPLY</field>
                <value name="A">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
                <value name="B">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
            </block>

            {/* <!-- Khối Chia (Division) --> */}
            <block type="math_division">
                <field name="OP">DIVIDE</field>
                <value name="A">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
                <value name="B">
                    {/* <!-- <shadow type="math_number">
                        <field name="NUM">1</field>
                    </shadow> --> */}
                </value>
            </block>
        </category>
        <category name="Biến" custom="VARIABLE">

        </category>

    </category>
  </BlocklyDrawer>,
  document.getElementById("root")
);
